def part1(inp):
    seats = []

    i = 0
    for line in inp:
        lowPointer = 0
        highPointer = 127
        
        for inst in line[:7]:
            mid = (lowPointer + highPointer) // 2
            
            if inst == "F":
                highPointer = mid

            elif inst == "B":
                lowPointer = mid
            
        row = highPointer

        lowPointer = 0
        highPointer = 7
        
        for inst in line[7:]:
            mid = (lowPointer + highPointer) // 2
            
            if inst == "L":
                highPointer = mid

            elif inst == "R":
                lowPointer = mid

        column = highPointer

        seats.append(row * 8 + column)

    seats.sort()

    return seats[len(seats)-1]

